listeners.add:
function(list, component, index) {

      var length = list.items.length;

	  var num = Math.floor(Math.random() * 90000) + 10000;


		  for(var i=0;i<=length;i++){

		  var item = list.items.items[i].items.items[0].items.items[0].items.items[1].value;
		  console.log(" item "+ list.items.items[i].items.items[0].items.items[0].items.items[0].value);
		  if(item == "" || item == null){
			list.items.items[i].items.items[0].items.items[0].items.items[1].setValue("tab_item_"+num);
		  }

		  }

		 /* list.items.each(function(i){
            
			console.log(i.fieldConfig.items["0"].key);

            //i.setValue(item[i.tabTitleText]);
        });*/
    }

